      "closed": "每年7月的第1个星期三及其隔日"
    },
    "ticket_prices": {
      "currency": "日元",
      "adult": 400,
      "high_school_student": 300,
      "middle_elementary_student": 160,
      "discount": "出示冲绳都市单轨电车1日或2日乘车券，可享团体价八折"
    },
    "transportation": {
      "monorail": {
        "from": "那霸机场站 (机场2楼通道)",
        "to": "首里站 (终点站)",
        "travel_time_approx": "30 分钟",
        "walk_from_station": "步行约15分钟至守礼门",
        "fare": {
          "adult": 330,
          "child": 170
        },
        "note": "如果当天行程会搭单轨在那霸市内移动，可以考虑购买1日自由乘车券。"
      },
      "bus": {
        "option_1_shortest_walk": {
          "line_numbers": ["7", "8"],
          "get_off_station": "首里城前站",
          "walk_time_approx": "步行约1分钟至守礼门",
          "note": "步行距离最短，但要注意班次、小心塞车。"
        },
        "option_2": {
          "line_numbers_city": ["1", "14"],
          "line_numbers_intercity": ["346"],
          "get_off_station": "首里城公园入口站",
          "walk_time_approx": "步行约5分钟至守礼门"
        },
        "option_3": {
          "line_numbers_city": ["9", "13"],
          "line_numbers_intercity": ["25", "97", "125"],
          "get_off_station": "山川站",
          "walk_time_approx": "步行约15分钟至守礼门"
        }
      },
      "driving": {
        "from_naha_airport_time_approx": "40 分钟",
        "parking": {
          "shurijo_castle_parking_fee": "2小时 320日元 (付费停车场)",
          "note": "周边也有不少投币式停车场。"
        }
      }
    },
    "points_of_interest": [
      {
        "name": "守礼门",
        "description": "首里城最著名城门，仿中国牌楼设计，悬挂「守礼之邦」匾额，象征冲绳。又名「首里门」、「上之绫门」。曾印于2000日元纪念纸钞。"
      },
      {
        "name": "欢会门",
        "description": "进入首里城城郭内的第一道正门，石造拱形，门上建有木造箭楼，兼具保卫和迎宾功能。两侧立有风狮爷石像。"
      },
      {
        "name": "奉神门",
        "description": "供奉神明之门，进入首里城正殿中庭广场「御庭」前的最后一道门。中式建筑风格，有左、中、右3个出入口（中央仅限王公贵族或中国使节等贵宾通行）。别名「君夸御门」。2019年大火后，屋瓦已经修复完毕，御庭也几乎恢复原貌。"
      },
      {
        "name": "广福门",
        "description": "木头结构，带有「福运广传」意涵，别称「长御门」。过去是中央行政办公室，现在是首里城售票处。是首里城内绝佳的赏景点，可欣赏龙潭池、弁财天堂、圆觉寺大门，远眺虎濑山与弁之岳。"
      },
      {
        "name": "西台",
        "description": "位于城郭西侧的展望台，海拔约100公尺，别名「岛添台」。是眺望城内、城外风景的最佳地点，可俯瞰那霸市区、那霸港，天气好时可遥望庆良间群岛。"
      },
      {
        "name": "素屋根见学エリア (临时屋顶参观区)",
        "description": "可以在此近距离观看正殿实际修复工程，搭配现场的解说板、火灾残留物和复原所使用木材的展示，了解首里城的修复情况和进展。"
      ]
    },
    "activities": [
      {
        "name": "御开门式",
        "description": "每天早上8点25分至45分在奉神门举行。身穿传统服饰的工作人员解说仪式，高喊「御开门」、敲铜锣并打开大门。早上御庭还会播放琉球宫廷音乐「御座乐」。"
      },
      {
        "name": "夜间点灯",
        "description": "每天日落后至深夜12点，首里城会点灯。可在城郭外悠闲漫步欣赏夜景。闭园后不能进入城内。园方停车场在21点关闭。"
      },
      {
        "name": "首里城复兴祭 (原首里城祭)",
        "description": "首里城最大活动，重现琉球王朝华丽荣光。包括传统琉球音乐舞蹈表演、「古式行列」（还原昔日祈求国家安康和五谷丰收的游行）以及国际通的「琉球王朝绘卷行列」（约700人穿戴华丽服饰游行，国王和王妃参加）。2024年预计11月举行，详细时间待主办单位公布。"
      }
    ],
    "reconstruction_status": {
      "fire_date": "2019年10月31日凌晨2时左右",
      "burned_buildings": ["南殿", "北殿", "正殿", "奉神门", "书院等"],
      "damaged_area": "总面积达3,813平方公尺",
      "estimated_loss": "建筑物加上收藏文物在内损失金额估计高达53亿日元",
      "cause": "起火原因不明 (根据事后调查，可能与延长线走火或LED照明设备供电异常有关，但找不到证物)",
      "reconstruction_focus": "防火 (加设烟热探测自动洒水装置、导入夜间微光监视系统、在地底下埋设送水管线)",
      "main_hall_completion_estimate": "2026年",
      "other_buildings_reconstruction": "正殿完成复原工程后，再陆续展开北殿和南殿等其他设施的修复重建"
    }
  }
}
